const axios = require('axios');
const api_key = process.env.REACT_APP_YOUTUBE_API_KEY;
const baseURL = 'https://www.googleapis.com/youtube/v3';


const sortChannelsBySubscriberCount = (channels) => {
  console.log(channels);
  channels.sort((a,b) => (a.statistics.subscriberCount > b.statistics.subscriberCount) ? 1 : -1);
}

export const searchChannels = (name) => {
  return axios.get(`${baseURL}/search?key=${api_key}&q=${name}&type=channel&part=snippet&maxResults=25`)
  .then(response => {

    const ids = response.data.items.map((item) => {
      return item.id.channelId;
    }) 

    // console.log(ids);

    const channels = ids.map((id) => {
      return axios.get(`${baseURL}/channels?key=${api_key}&part=snippet,contentDetails,contentOwnerDetails,id,statistics,status,topicDetails&id=${id}`)
      .then(result => {
        // console.log(result);
        return result.data;
      });
    })

    const channelsData = Promise.all(channels).then(data => {
      const channelDataDetails = data.map((detail) => {
        return detail.items[0];
      })

      sortChannelsBySubscriberCount(channelDataDetails);

      return channelDataDetails;
    });

    console.log(channelsData);

    // sortChannelsBySubscriberCount(channelsData)

    // return sortChannelsBySubscriberCount(channelsData);
    return channelsData;
  });
}
