import { Avatar, makeStyles } from "@material-ui/core";
import { LinearScale } from "@material-ui/icons";
import React from "react"; 
import "./styles/Artist.css";

const useStyles = makeStyles({
  section: props => ({
    backgroundImage: props.backgroundImage,
  }),
  avatar: {
    height: '200px',
    width: '200px',
    borderStyle: 'solid',
    borderColor: 'white',
    borderWidth: '3px',
  },
})


function Artist({artist}) {
  const props = { backgroundImage: artist.images[0].url }
  const classes = useStyles(props);


  return (
    <div className="artist__Section" >
      <Avatar src={artist.images[0].url} alt={artist.name} className={classes.avatar}/>
      <div className="artist__Info">
        <h2>{artist.name}</h2>
        <hr />
        <div className="artist__followers"> 
          <h2>Followers: {artist.followers.total}</h2>
        </div>
      </div>
    </div>
  )
}

export default Artist;
