import React from "react"; 
import "./styles/Body.css";
import { PlayCircleFilled, Favorite, MoreHoriz } from "@material-ui/icons"; 
import { useDataLayerValue } from "../../contexts/DataLayer";
import Header from "./Header";
import SongRow from "./SongRow";

function Body({spotify}) {
  const [{ discover_weekly, playlist }, dispatch] = useDataLayerValue();

  return (
    <div className="body">
      <Header spotify={spotify} /> 
      <div className="body__info">
        <img src = {discover_weekly?.images[0]?.url} alt="" />
        <img src = {playlist?.items[0]?.images[0]?.url} alt="" />
        <div className="body__infoText">
          <strong>PLAYLIST</strong>
          <h2>Discover Weekly</h2>
          <p>{discover_weekly?.description}</p>
          <strong>{playlist?.items[0]?.name}</strong>
          <h2>{playlist?.items[0]?.display_name}</h2>
          <p>{playlist?.items[0]?.description}</p>
        </div>
      </div>
      <div className="body__songs">
        <div className="body__icons">
          <PlayCircleFilled className="body__shuffle" />
          <Favorite fontSize="large" />
          <MoreHoriz />
        </div>
        {discover_weekly?.tracks.items.map((item) => (
          <SongRow track={item.track} />
        ))}
      </div>
    </div>
  );
}

export default Body;
