const Instagram = require('instagram-web-api'); 
const username = process.env.REACT_APP_INSTAGRAM_USERNAME; 
const password = process.env.REACT_APP_INSTAGRAM_PASSWORD;

const client = new Instagram({ username, password }, { proxy: "http://localhost:3000/" })

export const authorizeInstagram = async () => {
  await client.login()
  .then(() => {
    client.getProfile().then(console.log)
  })
  // const profile = await client.getProfile()

  console.log(profile);
}
