import React from "react"; 
import "./styles/Body.css";
import { PlayCircleFilled, Favorite, MoreHoriz } from "@material-ui/icons"; 
import { useDataLayerValue } from "../../contexts/DataLayer";
import Header from "./Header";
import SongRow from "./SongRow";

function Body({spotify}) {
  const [{ discover_weekly, playlists, current_playlist, tracks, artists, albums }, dispatch] = useDataLayerValue();
  console.log(playlists);
  console.log(discover_weekly);
  console.log(current_playlist);
  // console.log(current_playlist.tracks.items);

  const playPlaylist = (id) => {
    spotify.play({
      context_uri: `spotify:playlist:${discover_weekly.id}`,
    }).then(() => {
      spotify.getMyCurrentPlayingTrack().then((track) => {
        dispatch({
          type: 'SET_ITEM',
          item: track.item
        });
        dispatch({
          type: 'SET_PLAYING',
          playing: true,
        });
      });
    });
  }

  return (
    <div className="body">
      <Header spotify={spotify} /> 
      <div className="body__info">
        {current_playlist ? 
        <img src = {playlists.items ? playlists.items[0].images[0].url : null} alt="" /> :
        <img src = {discover_weekly?.images[0]?.url} alt="" /> }
        <div className="body__infoText">
          {current_playlist ? 
          <>
            <strong>PLAYLIST</strong>
            <h2>My Playlist </h2> 
            <p>{discover_weekly?.description}</p>
          </> :
          <>
            <strong>PLAYLIST</strong>
            <h2>Discover Weekly</h2>
            <p>{discover_weekly?.description}</p>
          </>
           }
        </div>
      </div>
      <div className="body__songs">
        <div className="body__icons">
          <PlayCircleFilled className="body__shuffle" onclick={playPlaylist}/>
          <Favorite fontSize="large" />
          <MoreHoriz />
        </div>
        {current_playlist ? 
        current_playlist.tracks.items.map((item) => (
          <SongRow track={item.track} key={item.track.id}/>
        )) : 
        discover_weekly?.tracks.items.map((item) => (
          <SongRow track={item.track} key={item.track.id} />
        ))}
      </div>
    </div>
  );
}

export default Body;
