const dotenv = require('dotenv').config()

const authEndpoint = "https://accounts.spotify.com/authorize";
const redirectUri = "https://localhost:3000/";
const clientId = process.env.SPOTIFY_CLIENT_ID;
// const clientId = "6c523cdfaa7a49209448e49a04aabc09"

const scopes = [
  "user-read-currently-playing",
  "user-read-recently-playing",
  "user-read-playback-state",
  "user-top-read",
  "user-modify-playback-state",
];

const loginUrl = `${authEndpoint}?client_id=${clientId}&redirect_uri=${redirectUri}&scope=${scopes.join("%20")}&response_type=token&show_dialog=true`;

module.exports = { 
  loginUrl,
  authEndpoint
}
